"use client";
import React from "react";

type Props = {
    role: string;
}

export function ProfileBanner({ role }: Props) {
    const getBannerColor = (role: string) => {
        const bannerColorMap: Record<string, string> = {
            ROLE_ADMIN: "from-purple-600 via-purple-500 to-pink-500",
            ROLE_CASHIER: "from-blue-600 via-blue-500 to-cyan-500",
            ROLE_WAITER: "from-green-600 via-green-500 to-teal-500",
            ROLE_MANAGER: "from-orange-600 via-orange-500 to-red-500"
        };
        return bannerColorMap[role] || "from-gray-600 via-gray-500 to-gray-400";
    };

    return (
        <div className={`relative h-48 bg-gradient-to-r ${getBannerColor(role)} overflow-hidden`}>
            <div className="absolute inset-0 bg-black/10"></div>
            <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-30"></div>
        </div>
    );
}